-- -- Units of conformance --
natConfigGroup OBJECT-GROUP OBJECTS { natInterfaceRealm, natInterfaceServiceType, natInterfaceStorageType, natInterfaceRowStatus, natAddrMapName, natAddrMapEntryType, natAddrMapTranslationEntity, natAddrMapLocalAddrType, natAddrMapLocalAddrFrom, natAddrMapLocalAddrTo, natAddrMapLocalPortFrom, natAddrMapLocalPortTo, natAddrMapGlobalAddrType, natAddrMapGlobalAddrFrom, natAddrMapGlobalAddrTo, natAddrMapGlobalPortFrom, natAddrMapGlobalPortTo, natAddrMapProtocol, natAddrMapStorageType, natAddrMapRowStatus, natBindDefIdleTimeout, natUdpDefIdleTimeout, natIcmpDefIdleTimeout, natOtherDefIdleTimeout, natTcpDefIdleTimeout, natTcpDefNegTimeout, natNotifThrottlingInterval } STATUS deprecated DESCRIPTION "A collection of configuration-related information required to support management of devices supporting NAT. Deprecated in favor of NATV2-MIB." REFERENCE "RFC 7658, RFC 7569" ::= { natMIBGroups 1 }
natTranslationGroup OBJECT-GROUP OBJECTS { natAddrBindNumberOfEntries, natAddrBindGlobalAddrType, natAddrBindGlobalAddr, natAddrBindId, natAddrBindTranslationEntity, natAddrBindType, natAddrBindMapIndex, natAddrBindSessions, natAddrBindMaxIdleTime, natAddrBindCurrentIdleTime, natAddrBindInTranslates, natAddrBindOutTranslates, natAddrPortBindNumberOfEntries, natAddrPortBindGlobalAddrType, natAddrPortBindGlobalAddr, natAddrPortBindGlobalPort, natAddrPortBindId, natAddrPortBindTranslationEntity, natAddrPortBindType, natAddrPortBindMapIndex, natAddrPortBindSessions, natAddrPortBindMaxIdleTime, natAddrPortBindCurrentIdleTime, natAddrPortBindInTranslates, natAddrPortBindOutTranslates, natSessionPrivateSrcEPBindId, natSessionPrivateSrcEPBindMode, natSessionPrivateDstEPBindId, natSessionPrivateDstEPBindMode, natSessionDirection, natSessionUpTime, natSessionAddrMapIndex, natSessionProtocolType, natSessionPrivateAddrType, natSessionPrivateSrcAddr, natSessionPrivateSrcPort, natSessionPrivateDstAddr, natSessionPrivateDstPort, natSessionPublicAddrType, natSessionPublicSrcAddr, natSessionPublicSrcPort, natSessionPublicDstAddr, natSessionPublicDstPort, natSessionMaxIdleTime, natSessionCurrentIdleTime, natSessionInTranslates, natSessionOutTranslates } STATUS deprecated DESCRIPTION "A collection of BIND-related objects required to support management of devices supporting NAT. Deprecated in favor of NATV2-MIB." REFERENCE "RFC 7658, RFC 7569" ::= { natMIBGroups 2 }
natStatsInterfaceGroup OBJECT-GROUP OBJECTS { natInterfaceInTranslates, natInterfaceOutTranslates, natInterfaceDiscards } STATUS deprecated DESCRIPTION "A collection of NAT statistics associated with the interface on which NAT is configured, to aid troubleshooting/monitoring of the NAT operation. Deprecated in favor of NATV2-MIB." REFERENCE "RFC 7658, RFC 7569" ::= { natMIBGroups 3 }
natStatsProtocolGroup OBJECT-GROUP OBJECTS { natProtocolInTranslates, natProtocolOutTranslates, natProtocolDiscards } STATUS deprecated DESCRIPTION "A collection of protocol-specific NAT statistics, to aid troubleshooting/monitoring of NAT operation. Deprecated in favor of NATV2-MIB." REFERENCE "RFC 7658, RFC 7569" ::= { natMIBGroups 4 }
natStatsAddrMapGroup OBJECT-GROUP OBJECTS { natAddrMapInTranslates, natAddrMapOutTranslates, natAddrMapDiscards, natAddrMapAddrUsed } STATUS deprecated DESCRIPTION "A collection of address-map-specific NAT statistics, to aid troubleshooting/monitoring of NAT operation. Deprecated in favor of NATV2-MIB." REFERENCE "RFC 7658, RFC 7569" ::= { natMIBGroups 5 }
natMIBNotificationGroup NOTIFICATION-GROUP NOTIFICATIONS { natPacketDiscard } STATUS deprecated DESCRIPTION "A collection of notifications generated by devices supporting this MIB. Deprecated in favor of NATV2-MIB." REFERENCE "RFC 7658, RFC 7569" ::= { natMIBGroups 6 }
