-- ## RSerPool type definitions ################################### RSerPoolENRPServerIdentifierTC ::= TEXTUAL-CONVENTION DISPLAY-HINT "x" STATUS current DESCRIPTION "The ID of an ENRP server" SYNTAX Unsigned32 (1..4294967295)
RSerPoolOperationScopeTC ::= TEXTUAL-CONVENTION DISPLAY-HINT "1024t" STATUS current DESCRIPTION "The ID of an operation scope" SYNTAX OCTET STRING (SIZE (0..65535))
RSerPoolPoolHandleTC ::= TEXTUAL-CONVENTION DISPLAY-HINT "1024t" STATUS current DESCRIPTION "The pool handle" SYNTAX OCTET STRING (SIZE (0..65535))
RserpoolPoolElementIdentifierTC ::= TEXTUAL-CONVENTION DISPLAY-HINT "x" STATUS current DESCRIPTION "The pool element ID" SYNTAX Unsigned32 (1..4294967295)
RSerPoolPolicyIdentifierTC ::= TEXTUAL-CONVENTION DISPLAY-HINT "x" STATUS current DESCRIPTION "The ID of the pool policy" SYNTAX Unsigned32 (1..4294967295)
RSerPoolPolicyLoadTC ::= TEXTUAL-CONVENTION DISPLAY-HINT "d" STATUS current DESCRIPTION "The load status of a pool element" SYNTAX Unsigned32 (0..4294967295)
RSerPoolPolicyWeightTC ::= TEXTUAL-CONVENTION DISPLAY-HINT "d" STATUS current DESCRIPTION "The weight of a pool element" SYNTAX Unsigned32 (0..4294967295)
RSerPoolTransportUseTypeTC ::= TEXTUAL-CONVENTION STATUS current DESCRIPTION "The transport use of a pool element" SYNTAX INTEGER { dataOnly(0), dataPlusControl(1) }
RSerPoolOpaqueAddressTC ::= TEXTUAL-CONVENTION DISPLAY-HINT "1024t" STATUS current DESCRIPTION "Opaque address" SYNTAX OCTET STRING (SIZE (0..65535))
